import React, {useState, useEffect} from 'react';
import TodoItem from "./todoitem";
import TodoService from "../services/todo.service"

function TodoList() {
    const [username, setUsername] = useState("jekagreka");
    const [items, setItems] = useState([]
    );

    function toggleItem(id) {
        let newItems = items.map(item => {
            if (item.id === id) {
                let changedItem = item;
                changedItem.done = !item.done;
                TodoService.updateItemDone(changedItem).then(() => {
                    console.log(item);
                    item.done = changedItem.done;
                    console.log(item);
                });

            }
            return item;
        });
        setItems(newItems);
    }

    useEffect(() => {
        TodoService.getTodoItems(username).then(response => {
            setItems(response.data)
        })

    }, [username])

    return (
        <div>
            <h1>welcome to todolist {username}</h1>
            {items.map((item) => {
                return <TodoItem item={item} key={item.id} toggleItem={toggleItem}/>
            })}
        </div>
    );
}

export default TodoList;
