const styles = {
    li: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '.5rem 2rem',
        border: '1px solid #ccc',
        borderRadius: '5px',
        marginBottom: '.5rem'
    }
}

function TodoItem({item, toggleItem}) {
    return (
        <li style={styles.li}>
          <span>
              <input type='checkbox' onChange={()=> toggleItem(item.id)}/>
              <strong>{items.index}</strong>
              &nbsp;
              {items.title}
          </span>
            <button>&times;</button>
        </li>
    );

}

export default TodoItem;
