const API_URL = 'http://localhost:8080/todolist';

class TodoService {
    addItem(item){
        return axios.post(API_URL, item)
    }

    getTodoItems(username) {
        return axios.get(API_URL, {
            params: {
                username: username
            }
        })
    };

    updateItemDone(item) {
        return axios.put(API_URL + '/done', item)
    };

    removeItem(id) {
        return axios.delete(API_URL, {
            params: {
                id: id
            }
        })
    }

    updateIndexes(items){
        return axios.put(API_URL + '/index', items)
    }


