import React, {useEffect, useState} from 'react';
import TodoItem from "./TodoItem";
import TodoService from "../services/todo.service"
import AuthService from "../services/auth.service"
import ItemForm from "./ItemForm";
import {DragDropContext, Draggable, Droppable} from "react-beautiful-dnd";
import {useHistory} from "react-router-dom";

function TodoList() {
    const [username, setUsername] = useState('');
    const [items, setItems] = useState([]);
    const history = useHistory()

    function addTag(tag) {
        let added = true;
        setItems(items.map(item => {
            if (item.id === tag.itemId) {
                if (item.tags) {
                    if (item.tags.length < 3)
                        item.tags.push(tag)
                    else {
                        console.log("too much tags");
                        added = false;
                        return item;
                    }
                } else item.tags = [tag]
            }
            return item
        }))
        if (added)
            TodoService.addTag(tag).catch(error => {
                console.log(error)
            })
    }

    function toggleItem(id) {
        setItems(items.map(item => {
            if (item.id === id) {
                item.done = !item.done;
                //do something on error (not connected to server)
                TodoService.updateItemDone(item).catch(error => {
                    //show error message
                    console.log(error);
                })
            }
            return item;
        }));
    }

    function removeItem(id) {
        //do something on error (not connected to server)
        setItems(items.filter(item => item.id !== id).map((item, index) => {
            item.index = index + 1;
            return item;
        }));
        TodoService.removeItem(id).then(() => {
            TodoService.updateIndexes(items).catch(error => {
                //show error message
                console.log(error);
            });
        }).catch(error => {
            //change connected
            console.log(error);
        });
    }

    function addItem(title) {
        let newItem = {title, done: false, index: items.length + 1, id: Date.now(), ownerName: username}
        setItems(items.concat([newItem]));
        TodoService.addItem(newItem).then(response => {
            let responseItem = response.data;
            setItems(items.concat([responseItem]));
        }).catch(error => {
            console.log(error);
        })
    }

    useEffect(() => {
        if (AuthService.getCurrentUser())
            setUsername(AuthService.getCurrentUser().username);
        TodoService.getTodoItems(username).then(response => {
            setItems(response.data);
        })
    }, [username])

    function onDragEnd(param) {
        const srcI = param.source.index;
        const desI = param.destination?.index;
        items.splice(desI, 0, items.splice(srcI, 1)[0]);
        setItems(items.map((item, index) => {
            item.index = index + 1;
            return item;
        }));
        TodoService.updateIndexes(items).catch(error => {
            //show error message
            console.log(error);
        });
    }

    return (
        <div>
            <h1>welcome to todolist {username}</h1>
            <ItemForm onCreate={addItem}/>
            <DragDropContext onDragEnd={(param) => onDragEnd(param)}>
                <Droppable droppableId="droppable-1">
                    {(provided, _) => (
                        <div ref={provided.innerRef} {...provided.droppableProps}>
                            {items.length ? items.map((item, i) => (
                                <Draggable
                                    key={item.id}
                                    draggableId={'draggable-' + item.id}
                                    index={i}>
                                    {(provided, snapshot) => (
                                        <div ref={provided.innerRef}
                                             {...provided.draggableProps} {...provided.dragHandleProps}
                                             style={{
                                                 ...provided.draggableProps.style,
                                                 borderRadius: '5px',
                                                 boxShadow: snapshot.isDragging
                                                     ? "0 0 .4rem #666"
                                                     : "none",
                                             }}>
                                            <TodoItem
                                                item={item}
                                                key={item.id} toggleItem={toggleItem}
                                                removeItem={removeItem}
                                                tags={item.tags} addTag={addTag}/>
                                        </div>
                                    )}
                                </Draggable>
                            )) : <p>No items</p>}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    );
}

export default TodoList;
