const styles = {
    li: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '.5rem 2rem',
        border: '1px solid #ccc',
        borderRadius: '5px',
        marginBottom: '.5rem'
    }
}

function TodoItem({item, toggleItem, removeItem}) {
    const classes = []

    if (item.done) {
        classes.push('done')
    }

    return (
        <li style={styles.li}>
          <span className={classes.join(' ')}>
              <input checked={item.done} type='checkbox' onChange={() => toggleItem(item.index)}/>
              <strong>{item.index}</strong>
              &nbsp;
              {item.title}
          </span>
            <button onClick={()=>removeItem(item.index)}>&times;</button>
        </li>
    );

}

export default TodoItem;
