import React, {useEffect, useState} from 'react';
import TodoItem from "./TodoItem";
import TodoService from "../services/todo.service"
import ItemForm from "./ItemForm";
import {DragDropContext, Draggable, Droppable} from "react-beautiful-dnd";

function TodoList() {
    const [username, setUsername] = useState("jake");
    const [items, setItems] = useState([]
    );

    function addTag(tag) {
        let added = true;
        setItems(items.map(item => {
            if (item.id === tag.itemId) {
                if (item.tags) {
                    if (item.tags.length < 3)
                        item.tags.push(tag)
                    else {
                        console.log("too much tags");
                        added = false;
                        return item;
                    }
                } else item.tags = [tag]
            }
            return item
        }))
        if (added)
            TodoService.addTag(tag).catch(error => {
                console.log(error)
            })
    }

    function toggleItem(id) {
        setItems(items.map(item => {
            if (item.id === id) {
                item.done = !item.done;
                //do something on error (not connected to server)
                TodoService.updateItemDone(item).catch(error => {
                    //show error message
                    console.log(error);
                })
            }
            return item;
        }));
    }

    function removeItem(id) {
        //do something on error (not connected to server)
        setItems(items.filter(item => item.id !== id).map((item, index) => {
            item.index = index + 1;
            return item;
        }));
        TodoService.removeItem(id).then(() => {
            TodoService.updateIndexes(items).catch(error => {
                //show error message
                console.log(error);
            });
        }).catch(error => {
            //change connected
            console.log(error);
        });
    }

    function addItem(title) {
        let newItem = {title, done: false, index: items.length + 1, id: Date.now(), ownerName: username}
        setItems(items.concat([newItem]));
        TodoService.addItem(newItem).then(response => {
            let responseItem = response.data;
            setItems(items.concat([responseItem]));
        }).catch(error => {
            console.log(error);
        })
    }

    useEffect(() => {
        TodoService.getTodoItems(username).then(response => {
            setItems(response.data);
        })
    }, [username])

    let onDragEnd = result => {
        //gh
    }

    return (
        <div>
            <h1>welcome to todolist {username}</h1>
            <ItemForm onCreate={addItem}/>
            <DragDropContext onDragEnd={(...props) => {
                console.log(this.props)
            }}>
                <Droppable droppableId="droppable-1">
                    {(provided, _) => (
                        <div ref={provided.innerRef} {...provided.droppableProps}>
                            {items.length ? items.map((item, i) => (
                                 <Draggable
                                    key={item.id}
                                    draggableId={'draggable-' + item.id}
                                    index={i}>
                                    {(provided, snapshot) => (
                                        <TodoItem
                                            ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}
                                            item={item}
                                            key={item.id} toggleItem={toggleItem}
                                            removeItem={removeItem}
                                            tags={item.tags} addTag={addTag}/>
                                    )}
                                </Draggable>
                                )) : <p>No items</p>}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    );
}

export default TodoList;
