import React, {useState, useEffect} from 'react';
import TodoItem from "./todoitem";
import TodoService from "../services/todo.service"

function TodoList() {
    const [username, setUsername] = useState("jekagreka");
    const [items, setItems] = useState([]
    );

    function toggleItem(id) {
        setItems(items.map(item => {
            if (item.id === id) {
                item.done = !item.done;
                //do something on error
                TodoService.updateItemDone(item).then(() => {
                        return undefined;
                    }
                ).catch(error => {
                    console.log(error);
                })

            }
            return item;
        }));
    }

    function removeItem(id) {
        items.filter(item => item.id !== id);

    }

    useEffect(() => {
        TodoService.getTodoItems(username).then(response => {
            setItems(response.data)
        })

    }, [username])

    return (
        <div>
            <h1>welcome to todolist {username}</h1>
            {items.map((item) => {
                return <TodoItem item={item} key={item.id} toggleItem={toggleItem}/>
            })}
        </div>
    );
}

export default TodoList;
